onClick:
{{
  // Controllo se siamo su BLU o VERDE
  appsmith.store.db_attivo === 'BLU' ? 

  // SE BLU
  Kill_Blu.run(() => {
      closeModal('Modal1'); // Chiudo il modale (controlla se si chiama Modal1 o Modal_Sicurezza)
      showAlert('Processo ' + appsmith.store.kill_id + ' terminato (BLU)', 'success');

      // Aggiorno la tabella ricaricando i dati
      appsmith.store.vista_attiva === 'MEDIC' ? Ba_Medic_Paideia.run() : Get_Blocchi_Paideia.run(); 
  }, () => showAlert('Errore Kill Blu', 'error')) 

  : 

  // SE VERDE
  Kill_Verde.run(() => {
      closeModal('Modal1'); 
      showAlert('Processo ' + appsmith.store.kill_id + ' terminato (VERDE)', 'success');

      // Aggiorno la tabella ricaricando i dati
      appsmith.store.vista_attiva === 'MEDIC' ? Ba_Medic_Verde.run() : Get_Blocchi_Verde.run();
  }, () => showAlert('Errore Kill Verde', 'error'))
}}
