onClick:
{{
  // Controllo se siamo su Paideia o Materdei
  appsmith.store.db_attivo === 'Paideia' ? 

  // --- CASO BLU ---
  Kill_Paideia.run(() => {
      closeModal('Modal1'); 
      showAlert('Processo ' + appsmith.store.kill_id + ' terminato (Paideia)', 'success');

      // Aggiorno la tabella corretta in base a cosa stavo guardando
      if (appsmith.store.vista_attiva === 'MEDIC') { Ba_Medic_Paideia.run(); }
      else if (appsmith.store.vista_attiva === 'BLOCCO_HADE') { Blocco_Hade_Paideia.run(); }
      else { Get_Blocchi_Paideia.run(); }

  }, () => showAlert('Errore Kill Paideia (Controlla Prepared Statements)', 'error')) 

  : 

  // --- CASO VERDE ---
  Kill_Materdei.run(() => {
      closeModal('Modal1'); 
      showAlert('Processo ' + appsmith.store.kill_id + ' terminato (Materdei)', 'success');

      // Aggiorno la tabella corretta
      if (appsmith.store.vista_attiva === 'MEDIC') { Ba_Medic_Materdei.run(); }
      else if (appsmith.store.vista_attiva === 'BLOCCO_HADE') { Blocco_Hade_Materdei.run(); }
      else { Get_Blocchi_Materdei.run(); }

  }, () => showAlert('Errore Kill Materdei (Controlla Prepared Statements)', 'error'))
}}
