onClick:
{{
(() => {
    // 1. PULIZIA TOTALE (Visiva, Memoria ed Errori)
    if (get_fatture.clear) { get_fatture.clear(); } 
    storeValue('fattureDaModificare', []);
    storeValue('errore_sp', ''); // <--- AGGIUNTO QUI: Pulisce il box rosso

    // 2. LOGICA PRIORITÀ
    if (Input_Cartella.text) {

        // CASO CARTELLA ESISTENTE:
        // Uso .then() per aspettare la fine della cancellazione
        resetWidget('Input_CF', true).then(() => {
            // Parte SOLO ora, quando il CF è sicuramente vuoto
            get_fatture.run();
        });

    } else if (Input_CF.text) {

        // CASO SOLO CF (Cartella è vuota):
        get_fatture.run();

    } else {

        // CASO TUTTO VUOTO
        showAlert('⚠️ Nessun dato inserito (Scrivi CF o Cartella)', 'error');
    }
})()
}}
