onClick:
(() => {
    // 1. PULIZIA TOTALE (Visiva, Memoria ed Errori)
    if (get_fatture.clear) { get_fatture.clear(); } 
    storeValue('fattureDaModificare', []);
    storeValue('errore_sp', '');

    // 2. LOGICA PRIORITÀ
    if (Input_Cartella.text) {

        // --- CASO A: CARTELLA ESISTENTE ---
        resetWidget('Input_CF', true);
        // resetWidget('Input_NumeroFattura', true); // Scommenta se vuoi pulire anche la fattura

        get_fatture.run();

    } else if (Input_CF.text) {

        // --- CASO B: SOLO CF ---
        get_fatture.run();

    } else if (Input_NumeroFattura.text) {  // <--- ECCO L'ERRORE CORRETTO (Era Input_Fattura)

        // --- CASO C: SOLO FATTURA ---
        get_fatture.run();

    } else {

        // --- CASO D: TUTTO VUOTO ---
        showAlert('⚠️ Nessun dato inserito (Scrivi CF, Cartella o N°Fattura)', 'error');
    }
})()
