onClick:
(() => {
    // 1. Controllo preliminare campo vuoto
    if (!InputUsername.text) {
        showAlert('⚠️ Inserisci l\'utente H2O prima di procedere.', 'error');
        return;
    }

    // --- FASE 1: CONTROLLO AUTORIZZAZIONE (Query SQL) ---
    check_auth_user.run(() => {

        // Recuperiamo il risultato dalla query
        const authResult = check_auth_user.data[0];

        // Se lo stato NON è 'CONNESSO', blocchiamo tutto e mostriamo l'errore specifico del DB
        if (!authResult || authResult.Stato !== 'CONNESSO') {
            const messaggioErrore = authResult ? authResult.Stato : "Errore sconosciuto nel controllo utente";
            showAlert(`⛔ ${messaggioErrore}`, 'error'); 
            return; // STOP QUI
        }

        // --- FASE 2: UTENTE AUTORIZZATO -> PROCEDIAMO ---

        // A. Controllo SELEZIONE IVA
        if (!NUOVA_IVA.selectedOptionValue) {
            showAlert('⚠️ Errore: Devi selezionare un codice IVA dal menu a tendina!', 'error');
            return;
        }

        // B. Controllo SELEZIONE RIGHE (Verifica che la tabella si chiami Table2)
        if (!Table2.selectedRows || Table2.selectedRows.length === 0) {
            showAlert('⚠️ Attenzione: Non hai selezionato nessuna riga da modificare!', 'warning');
            return;
        }

        // --- FASE 3: ESECUZIONE DELL'UPDATE ---
        const codiceIvaScelto = NUOVA_IVA.selectedOptionValue;
        const righeSelezionate = Table2.selectedRows;

        // Mappiamo le righe selezionate per aggiornarle una ad una
        const processiDiUpdate = righeSelezionate.map(riga => {
            return update_iva_riga.run({
                nuovo_codice: codiceIvaScelto,
                id_fattura: riga.ININVOICEID,
                num_riga: riga.CPROWORD
            });
        });

        // Attendiamo che tutti gli aggiornamenti finiscano
        Promise.all(processiDiUpdate)
            .then(() => {
                showAlert(`✅ Successo! Modificate ${righeSelezionate.length} righe. Utente: ${InputUsername.text}`, 'success');

                // Aggiorniamo la vista
                get_dettaglio_fattura.run(); 

                // Opzionale: deseleziona le righe
                resetWidget('Table2', true);
            })
            .catch((err) => {
                console.log(err);
                showAlert('❌ Errore durante il salvataggio dei dati.', 'error');
            });

    }, () => {
        // Se la query check_auth_user fallisce tecnicamente (es. timeout o errore sintassi)
        showAlert('❌ Errore critico: Impossibile verificare l\'utente.', 'error');
    });
})()
