onClick:
{{
  (async () => {
    try {
        // 1. PULIZIA E CONTROLLI PRELIMINARI
        storeValue('errore_sp', '');

        if (!InputUsername.text) {
            throw new Error("⚠️ Inserisci l'utente H2O prima di procedere.");
        }

        // 2. ESEGUI LA QUERY DI CONTROLLO (E ASPETTA IL RISULTATO)
        // L'errore 'await' si risolve grazie a (async () => { ... })
        await check_auth_user.run();

        // 3. ANALISI DEL RISULTATO
        // Controlliamo se la query ha restituito dati validi
        if (!check_auth_user.data || check_auth_user.data.length === 0) {
             throw new Error("❌ Errore critico: La query utente non ha restituito risultati.");
        }

        const authResult = check_auth_user.data[0];

        // Se l'utente NON è connesso, blocchiamo tutto
        if (authResult.Stato !== 'CONNESSO') {
            throw new Error(`⛔ ${authResult.Stato}`); // Es: "UTENTE H2O ERRATO"
        }

        // --- SE ARRIVIAMO QUI, L'UTENTE È CONNESSO ---

        // 4. CONTROLLI AGGIORNAMENTO (IVA e RIGHE)
        if (!NUOVA_IVA.selectedOptionValue) {
            throw new Error("⚠️ Seleziona un codice IVA dal menu a tendina!");
        }

        if (!Table2.selectedRows || Table2.selectedRows.length === 0) {
            showAlert('⚠️ Seleziona almeno una riga dalla tabella!', 'warning');
            return; // Usciamo senza errore, è solo un avviso
        }

        // 5. PREPARAZIONE AGGIORNAMENTO MASSIRO
        const codiceIvaScelto = NUOVA_IVA.selectedOptionValue;
        const righeSelezionate = Table2.selectedRows;

        // Mappiamo le operazioni di aggiornamento
        const aggiornamenti = righeSelezionate.map(riga => {
            return update_iva_riga.run({
                nuovo_codice: codiceIvaScelto,
                id_fattura: riga.ININVOICEID,
                num_riga: riga.CPROWORD
            });
        });

        // 6. ESECUZIONE (ASPETTIAMO CHE TUTTE LE RIGHE SIANO AGGIORNATE)
        await Promise.all(aggiornamenti);

        // 7. SUCCESSO
        showAlert(`✅ Aggiornate ${righeSelezionate.length} righe. Utente: ${InputUsername.text}`, 'success');

        // Ricarichiamo la tabella e puliamo la selezione
        await get_dettaglio_fattura.run();
        resetWidget('Table2', true);

    } catch (err) {
        // --- GESTIONE ERRORI UNIFICATA ---
        // Qualsiasi errore (SQL o logico) finisce qui e viene mostrato nel box rosso
        const messaggio = err.message || JSON.stringify(err);
        storeValue('errore_sp', messaggio);
        showAlert(messaggio, 'error');
    }
  })()
}}
